// No in browser stuff from design of app

import './code-cell.css';
import React, { useEffect } from 'react';
import CodeEditor from './code-editor';
import Preview from './preview';
import Resizable from "./resizable";
import { Cell } from '../state';
import { useActions} from "../hooks/use-actions";
import { useTypedSelector } from "../hooks/use-typed-selector";

interface CodeCellProps {
    cell: Cell;
}

const CodeCell: React.FC<CodeCellProps> = ({ cell }) => {
    const {updateCell, createBundle} = useActions();
    const bundle = useTypedSelector((state) => state.bundles[cell.id]);

    // creates array of code strings for lower cells to access previous cells code
    const cumulativeCode = useTypedSelector((state) => {
        const {data, order} = state.cells;
        const orderedCells = order.map((id) => data[id]);

        // allows values to print in preview
        // React + Dom is added to make sure react components chan show with show()
        const showFunc = `
        import _React from 'react';
        import _ReactDOM from 'react-dom';
        var show = (value) => {
          const root = document.querySelector('#root');

          if (typeof value === 'object') {
            if (value.$$typeof && value.props) {
              _ReactDOM.render(value, root);
            } else {
              root.innerHTML = JSON.stringify(value);
            }
          } else {
            root.innerHTML = value;
          }
        };
      `;

        const showFuncNoop = 'var show = () => {}';

        const cumulativeCode = [];
        for (let c of orderedCells) {
            if(c.type === 'code') {
                if (c.id === cell.id) {
                    cumulativeCode.push(showFunc);
                } else {
                    cumulativeCode.push(showFuncNoop)
                }
                cumulativeCode.push(c.content);
            }
            //checks if current cell
            if (c.id === cell.id) {
                break;
            }
        }
        return cumulativeCode;
    });

    console.log(cumulativeCode);

    //bundling process
    useEffect(()=> {

        // if no bundle / if on load up
        if (!bundle) {
            createBundle(cell.id, cumulativeCode.join('\n'));
            return;
        }

        const timer = setTimeout(async ()=> {
            createBundle(cell.id, cumulativeCode.join('\n'));
        }, 750);

        return() => {
            clearTimeout(timer);
        };
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [cumulativeCode.join('\n'), cell.id, createBundle]);

    return (
        <Resizable direction={"vertical"}>
            {/* 'calc(100% - 10px)' has to have spaces in numbers otherwise doesnt work
                   This was note to self for future referance */}
            <div style={{height: 'calc(100% - 10px)', display: 'flex', flexDirection: 'row'}}>
                <Resizable direction={"horizontal"}>
                    <CodeEditor
                        initalValue={cell.content}
                        onChange={(value) => updateCell(cell.id, value)}
                    />
                </Resizable>
                <div className={"progress-wrapper"}>
                {!bundle || bundle.loading === true ? (
                    <div className={"progress-cover"}>
                        <progress className={"progress is-small is-prumary"} max="100">
                            Loading
                        </progress>
                    </div>
                ) : (
                    <Preview code={bundle.code} bundlingStatus={bundle.err} />
                )}
                </div>
            </div>
        </Resizable>
    );
};

export default CodeCell;
